mapper.select:
        SELECT * FROM y_comp

        	 AND COMP_KEY = #{COMP_KEY}
			 AND COMP_NAME = #{COMP_NAME}
			 AND COMP_INTRO = #{COMP_INTRO}
			 AND COMP_ADDR = #{COMP_ADDR}
			 AND COMP_OADDR = #{COMP_OADDR}
			 AND COMP_NADDR = #{COMP_NADDR}
			 AND COMP_LAT = #{COMP_LAT}
			 AND COMP_LON = #{COMP_LON}
			 AND COMP_DETAIL_ADDR = #{COMP_DETAIL_ADDR}
			 AND COMP_TEL = #{COMP_TEL}
			 AND COMP_OPEN_TIME = #{COMP_OPEN_TIME}
			 AND COMP_CLOSE_TIME = #{COMP_CLOSE_TIME}
			 AND COMP_CREATE = #{COMP_CREATE}
			 AND COMP_MODI = #{COMP_MODI}


        ORDER BY comp_KEY DESC

        SELECT COUNT(*) FROM y_comp

			 AND COMP_KEY = #{COMP_KEY}
			 AND COMP_NAME = #{COMP_NAME}
			 AND COMP_INTRO = #{COMP_INTRO}
			 AND COMP_ADDR = #{COMP_ADDR}
			 AND COMP_OADDR = #{COMP_OADDR}
			 AND COMP_NADDR = #{COMP_NADDR}
			 AND COMP_LAT = #{COMP_LAT}
			 AND COMP_LON = #{COMP_LON}
			 AND COMP_DETAIL_ADDR = #{COMP_DETAIL_ADDR}
			 AND COMP_TEL = #{COMP_TEL}
			 AND COMP_OPEN_TIME = #{COMP_OPEN_TIME}
			 AND COMP_CLOSE_TIME = #{COMP_CLOSE_TIME}
			 AND COMP_CREATE = #{COMP_CREATE}
			 AND COMP_MODI = #{COMP_MODI}


        ORDER BY comp_KEY DESC

mapper.update:
UPDATE y_comp
COMP_KEY = #{COMP_KEY}, COMP_NAME = #{COMP_NAME}, COMP_INTRO = #{COMP_INTRO}, COMP_ADDR = #{COMP_ADDR}, COMP_OADDR = #{COMP_OADDR}, COMP_NADDR = #{COMP_NADDR}, COMP_LAT = #{COMP_LAT}, COMP_LON = #{COMP_LON}, COMP_DETAIL_ADDR = #{COMP_DETAIL_ADDR}, COMP_TEL = #{COMP_TEL}, COMP_OPEN_TIME = #{COMP_OPEN_TIME}, COMP_CLOSE_TIME = #{COMP_CLOSE_TIME}, COMP_CREATE = #{COMP_CREATE}, COMP_MODI = #{COMP_MODI},
WHERE comp_KEY = #{comp_KEY}
