mapper.select:
        SELECT 
        	*,
        	DATE_FORMAT(RESERV_DATE, '%Y-%m-%d') AS RESERV_DATE_NM,
        	DATE_FORMAT(RESERV_TIME, '%H:%i') AS RESERV_TIME_NM,
			DATE_FORMAT(RESERV_CREATE, '%Y-%m-%d') AS RESERV_CREATE_NM 
        FROM y_reserv res
        LEFT JOIN y_room ro ON ro.ROOM_KEY = res.ROOM_KEY

        	 AND RESERV_KEY = #{RESERV_KEY}
			 AND ROOM_KEY = #{ROOM_KEY}
			 AND USR_KEY = #{USR_KEY}
			 AND COMP_KEY = #{COMP_KEY}
			 AND RESERV_DATE = #{RESERV_DATE}
			 AND RESERV_TIME = #{RESERV_TIME}
			 AND RESERV_USR_NAME = #{RESERV_USR_NAME}
			 AND RESERV_USR_TEL = #{RESERV_USR_TEL}
			 AND RESERV_USR_NUM = #{RESERV_USR_NUM}
			 AND RESERV_STATE = #{RESERV_STATE}
			 AND RESERV_CREATE = #{RESERV_CREATE}
			 AND COMP_DETAIL_ADDR = #{COMP_DETAIL_ADDR}


				AND DATE(RESERV_DATE) >=
				#{RESERV_DATE_ST}


				AND DATE(RESERV_DATE) <=
				#{RESERV_DATE_ED}


        ORDER BY RESERV_KEY DESC

mapper.update:
UPDATE y_reserv
RESERV_KEY = #{RESERV_KEY}, ROOM_KEY = #{ROOM_KEY}, USR_KEY = #{USR_KEY}, COMP_KEY = #{COMP_KEY}, RESERV_DATE = #{RESERV_DATE}, RESERV_TIME = #{RESERV_TIME}, RESERV_USR_NAME = #{RESERV_USR_NAME}, RESERV_USR_TEL = #{RESERV_USR_TEL}, RESERV_USR_NUM = #{RESERV_USR_NUM}, RESERV_STATE = #{RESERV_STATE}, RESERV_CREATE = #{RESERV_CREATE}, COMP_DETAIL_ADDR = #{COMP_DETAIL_ADDR},
WHERE RESERV_KEY = #{RESERV_KEY}
