mapper.select:
SELECT *, 
	DATE_FORMAT(USR_CREATE, '%Y-%m-%d') AS USR_CREATE_NM,
	DATE_FORMAT(USR_DELETE_DATE, '%Y-%m-%d') AS USR_DELETE_DATE_NM
FROM
y_usr

	 AND USR_KEY = #{USR_KEY}
	 AND USR_ID = #{USR_ID}
	 AND USR_NAME = #{USR_NAME}
	 AND USR_EMAIL = #{USR_EMAIL}
	 AND USR_TEL = #{USR_TEL}
	 AND USR_STATE = #{USR_STATE}
	 AND USR_SNS_WAY = #{USR_SNS_WAY}
	 AND USR_CHECK_EMAIL = #{USR_CHECK_EMAIL}
	 AND USR_FCM = #{USR_FCM}


		AND DATE(USR_CREATE) >=
		#{USR_CREATE_ST}


		AND DATE(USR_CREATE) <=
		#{USR_CREATE_ED}


	AND (USR_NAME LIKE CONCAT(#{KEYWORD}, '%') OR USR_TEL
		LIKE CONCAT(#{KEYWORD}, '%'))


ORDER BY USR_KEY DESC

mapper.update:
UPDATE y_usr
USR_KEY = #{USR_KEY}, USR_ID = #{USR_ID}, USR_NAME = #{USR_NAME}, USR_EMAIL = #{USR_EMAIL}, USR_TEL = #{USR_TEL}, USR_STATE = #{USR_STATE}, USR_CREATE = #{USR_CREATE}, USR_SNS_WAY = #{USR_SNS_WAY}, USR_CHECK_EMAIL = #{USR_CHECK_EMAIL}, USR_FCM = #{USR_FCM}, USR_DELETE_DATE = NOW(),
WHERE USR_KEY = #{USR_KEY}
